            
package com.pack1;

import com.pack1.NumberListService;
import java.util.ArrayList;
import java.util.List;
import com.pack1.NumberService;
import javax.ejb.EJB;
import javax.ejb.Stateless;


@Stateless (mappedName="NumberListJNDIName")
public class NumberListServiceImplementation implements NumberListService {
@EJB(mappedName="NumberServiceJNDIName")
private NumberService numService;
    @Override
    public List<Double> getNumbers(int size, double range) {
       List<Double>  nums = new ArrayList<Double>();
       for(int i = 0;i<size;i++){
      nums.add(numService.getNumber(range));
}
return(nums);
    }
    
}
